function LayoutComponents( components, layout, data )
{
	var self = this;

	self.layout		= layout;
	self.components	= components;
	self.data		= data;

	self.layout_container		= document.getElementById( 'layout' );
	self.container				= document.getElementById( 'layoutcomponents' );
	self.component_container	= document.getElementById( 'components' );

	self.Initialize();
}

LayoutComponents.prototype.LayoutHTML = function()
{
	this.layout_name = document.getElementById( 'layout-name' );
	this.layout_name.innerHTML = this.layout.name;
}

LayoutComponents.prototype.Initialize = function()
{
	var self = this;

	self.LayoutHTML();

	if ( self.components.length > 0 )
	{
		for (var i = 0, componentsLength = self.components.length; i < componentsLength; i++)
		{
			self.BuildComponent( self.components[ i ] );
		}
	}

	if ( self.data.length > 0 ) 
	{
		for (var i = 0, dataLength = self.data.length; i < dataLength; i++)
		{
			self.BuildLayoutComponent( self.data[ i ] );
		}
	}

	self.Sortable( self.container );

	new Sortable( self.component_container, {
		group: {
			name: 'nested',
			pull: 'clone',
			put: false
		},
		sort: false,
		animation: 150,
		onEnd: function( evt ){
			if ( evt.item.parentNode === self.component_container )
			{
				return;
			}

			evt.item.classList.add( 'layout-component' );
			evt.item.classList.remove( 'component' );

			// setTimeout(function() {
			// 	// if you click cancel
			// 	evt.item.remove();
			// }, 2000);
		}
	});
}

LayoutComponents.prototype.BuildComponent = function( component )
{
	var elem = document.createElement( 'div' );

	elem.layout_component = {};
	elem.layout_component.component = component;

	elem.classList.add( 'component' );
	elem.innerHTML = '<span class="handle"></span>' + elem.layout_component.component.name;

	this.component_container.appendChild( elem );
}

LayoutComponents.prototype.BuildLayoutComponent = function( layout_component )
{
	var self = this;
	var children_elem;
	var elem = document.createElement( 'div' );

	elem.layout_component = layout_component;

	elem.classList.add( 'layout-component', 'list-group-item' );
	elem.innerHTML = '<span class="handle"></span>' + elem.layout_component.name;


	if ( layout_component.parent_element )
	{
		layout_component.parent_element.appendChild( elem );
	}
	else
	{
		self.container.appendChild( elem );
	}

	if ( layout_component.component.alw_chldrn === 1 )
	{
		children_elem = document.createElement( 'div' );
		children_elem.classList.add( 'nested-sortable', 'list-group' );
		elem.appendChild( children_elem );

		self.Sortable( children_elem );
	}


	if ( layout_component.children_count > 0 )
	{
		for (var i = 0; i < layout_component.children_count; i++)
		{
			layout_component.children[ i ].parent_element = children_elem;
			self.BuildLayoutComponent( layout_component.children[ i ] );
		}
	}
}

LayoutComponents.prototype.Sortable = function( elem )
{
	var self = this;

	new Sortable( elem, {
		group: {
			name: 'nested'
		},
		animation: 150,
		fallbackOnBody: true,
		swapThreshold: 0.65,
		handle: '.handle',
		draggable: '.layout-component',
		scroll: self.container
	});
}

